SELECT qry_CC_Channel.*, IIf([Result] In ('A','R','D','C'),1,0) AS Finalized, IIf([Result] In ('A','R','D'),1,0) AS FinalizedNetCancel, IIf([Result]='A',1,0) AS Appr, IIf(([Result]='A' And Left([New/Exist],1)='N'),1,0) AS ApprNew, IIf(([Result]='A' And Left([New/Exist],1)='N'),[Approve_Amount],0) AS Credit_Limit_New, IIf(([Result]='A' And Left([Criteria_Code_C],1)="0"),1,0) AS ApprNewCust, IIf(([Result]='A' And Left([New/Exist],1)='N' And [Act_status]='Yes'),1,0) AS Activate, IIf(([Result]='A' And Left([New/Exist],1)='N' And [No_Transaction_Date]<=60),1,0) AS Active60, Switch((Credit_Limit_New>=20000 And Credit_Limit_New<=39999),'20k-39k',(Credit_Limit_New>=40000 And Credit_Limit_New<=59999),'40k-59k',(Credit_Limit_New>=60000 And Credit_Limit_New<=79999),'60k-79k',(Credit_Limit_New>=80000 And Credit_Limit_New<=99999),'80k-99k',(Credit_Limit_New>=100000 And Credit_Limit_New<=119999),'100k-119k',(Credit_Limit_New>=120000 And Credit_Limit_New<=139999),'120k-139k',(Credit_Limit_New>=140000 And Credit_Limit_New<=159999),'140k-159k',(Credit_Limit_New>=160000),'>160k') AS Credit_Limit_New_Range, Switch((AGE>=10 And AGE<=19),'10-19',(AGE>=20 And AGE<=26),'20-26',(AGE>=27 And AGE<=30),'27-30',(AGE>=31 And AGE<=40),'31-40',(AGE>=41 And AGE<=50),'41-50',(AGE>=51 And AGE<=60),'51-60',(AGE>=61 And AGE<=90),'61-90') AS Cus_Age_Range, Switch((AGE<=26 And Income_Range='30,000 up'),'FJ&Inc>=30k',(AGE<=26 And Income_Range<>'30,000 up'),'First Jobber',(AGE>26 And Income_Range='30,000 up'),'Inc>=30k',True,'Mass') AS Cus_Segment, KTC_FrontEnd_Occupation_Code.DESC AS Occupation_Desc, KTC_Province_Type.BKK_UPC, KTC_Province_Type.Region, KTC_Province_Type.Sub_Region, KTC_Province_Type.Province_Thai, KTC_Province_Type.Province_Eng, KTC_Province_Type.Province_Type, KTC_Province_Type.strategic
FROM (qry_CC_Channel LEFT JOIN KTC_FrontEnd_Occupation_Code ON qry_CC_Channel.occupation_code=KTC_FrontEnd_Occupation_Code.code) LEFT JOIN KTC_Province_Type ON qry_CC_Channel.zipcode=KTC_Province_Type.zip_code;
