SELECT qry_RL_Channel.*, IIf([Result] In ('A','R','D','C'),1,0) AS Finalized, IIf([Result] In ('A','R','D'),1,0) AS FinalizedNetCancel, IIf([Result]='A',1,0) AS Appr, IIf([Result]='A' And [Criteria_Code]='0000',1,0) AS ApprNew, IIf(([Result]='A' And Left([Criteria_Code_C],1)="0"),1,0) AS ApprNewCust, IIf(([Result]='A' And [blk_Code]='VX' And [DAY_CLOSE]<=60),1,0) AS VX60, IIf(([Result]='A' And [blk_Code]<>'VX' And [BALANCE]<=1 And [DAY_CLOSE]<=60),1,0) AS [Bal<=0_60], IIf(([Result]='A' And [blk_Code]<>'VX' And [BALANCE]<=1 And [DAY_CLOSE]<=30),1,0) AS [Bal<=0_1-30], IIf(([Result]='A' And [blk_Code]<>'VX' And [BALANCE]<=1 And [DAY_CLOSE]>30 And [DAY_CLOSE]<=60),1,0) AS [Bal<=0_31-60], IIf((VX60=1 Or [Bal<=0_60]=1),1,0) AS VXAndEarly60, IIf([Result]='A',[Approve_Amount],0) AS Credit_Limit, SWITCH((Credit_Limit>=20000 And Credit_Limit<=39999),'20k-39k',(Credit_Limit>=40000 And Credit_Limit<=59999),'40k-59k',(Credit_Limit>=60000 And Credit_Limit<=79999),'60k-79k',(Credit_Limit>=80000 And Credit_Limit<=99999),'80k-99k',(Credit_Limit>=100000 And Credit_Limit<=119999),'100k-119k',(Credit_Limit>=120000 And Credit_Limit<=139999),'120k-139k',(Credit_Limit>=140000 And Credit_Limit<=159999),'140k-159k',(Credit_Limit>=160000),'>160k') AS Credit_Limit_Range, IIf([Result]='A',[Money_Transfer],0) AS FDD, IIf([Result]='A',[FDD]/[Credit_Limit]*100,Null) AS per_FDDbyCreditLimit, SWITCH((FDD=5000),'5k',(FDD=10000),'10k',(FDD>=10000 And FDD<=14999),'10k-14k',(FDD>=15000 And FDD<=19999),'15k-19k',(FDD>=20000 And FDD<=24999),'20k-24k',(FDD>=25000 And FDD<=29999),'25k-29k',(FDD>=30000),'>=30k') AS FDD_Range, Switch((AGE>=10 And AGE<=19),'10-19',(AGE>=20 And AGE<=26),'20-26',(AGE>=27 And AGE<=30),'27-30',(AGE>=31 And AGE<=40),'31-40',(AGE>=41 And AGE<=50),'41-50',(AGE>=51 And AGE<=60),'51-60',(AGE>=61 And AGE<=90),'61-90') AS Cus_Age_Range, Switch((AGE<=26 And Income_Range='30,000 up'),'FJ&Inc>=30k',(AGE<=26 And Income_Range<>'30,000 up'),'First Jobber',(AGE>26 And Income_Range='30,000 up'),'Inc>=30k',True,'Mass') AS Cus_Segment, KTC_FrontEnd_Occupation_Code.DESC AS Occupation_Desc, KTC_Province_Type.BKK_UPC, KTC_Province_Type.Region, KTC_Province_Type.Sub_Region, KTC_Province_Type.Province_Thai, KTC_Province_Type.Province_Eng, KTC_Province_Type.Province_Type, KTC_Province_Type.strategic
FROM (qry_RL_Channel LEFT JOIN KTC_FrontEnd_Occupation_Code ON qry_RL_Channel.Occupation_Code=KTC_FrontEnd_Occupation_Code.Code) LEFT JOIN KTC_Province_Type ON qry_RL_Channel.zipcode=KTC_Province_Type.zip_code;
